SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_aprovacao_multinivel ( nr_seq_lote_p bigint, nm_usuario_p text) AS $body$
DECLARE



qt_nivel_usua_logado_w		bigint;
count_reg_w			bigint;
count_aprov_w			bigint;
dt_aprovacao_w			timestamp;
qt_nivel_liberacao		lote_orgao_cobr_aprovacao.qt_nivel_liberacao%type;
ds_historico_w			varchar(255);
qt2_w				bigint;
qt1_w				bigint;


BEGIN

select	coalesce(max(a.qt_nivel_liberacao),0),
	max(dt_aprovacao)
into STRICT 	qt_nivel_usua_logado_w,
	dt_aprovacao_w
from	lote_orgao_cobr_aprovacao a
where	a.nr_seq_lote		= nr_seq_lote_p
and 	a.nm_usuario_aprov	= nm_usuario_p;

select	count(*)
into STRICT 	count_reg_w
from	lote_orgao_cobr_aprovacao a
where	a.nr_seq_lote		= nr_seq_lote_p
and 	a.qt_nivel_liberacao 	< qt_nivel_usua_logado_w;

select	count(*)
into STRICT 	count_aprov_w
from	lote_orgao_cobr_aprovacao a
where	a.nr_seq_lote		= nr_seq_lote_p
and 	(a.dt_aprovacao IS NOT NULL AND a.dt_aprovacao::text <> '')
and 	a.qt_nivel_liberacao 	< qt_nivel_usua_logado_w;

if (qt_nivel_usua_logado_w = 0)then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(336124);
end if;

if (dt_aprovacao_w IS NOT NULL AND dt_aprovacao_w::text <> '')then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(336173);
end if;

if (count_reg_w = count_aprov_w) then

	update	lote_orgao_cobr_aprovacao a
	set	a.dt_aprovacao		= clock_timestamp()
	where	a.nr_seq_lote		= nr_seq_lote_p
	and 	a.nm_usuario_aprov	= nm_usuario_p
	and 	a.qt_nivel_liberacao	= qt_nivel_usua_logado_w;

	ds_historico_w := wheb_mensagem_pck.get_texto(337764, 'NM_USUARIO=' || nm_usuario_p || ';DT_ATUALIZACAO=' || to_char(clock_timestamp(),'dd/MM/yyyy hh24:mi:ss'));
	CALL gerar_historico_cobr_orgao(null,null,nr_seq_lote_p,ds_historico_w,nm_usuario_p,'E');

else
	CALL wheb_mensagem_pck.exibir_mensagem_abort(336154);
end if;

select	count(*)
into STRICT	qt1_w
from	lote_orgao_cobr_aprovacao
where	nr_seq_lote = nr_seq_lote_p;

select	count(*)
into STRICT	qt2_w
from	lote_orgao_cobr_aprovacao
where	nr_seq_lote = nr_seq_lote_p
and	(dt_aprovacao IS NOT NULL AND dt_aprovacao::text <> '');

if (	qt1_w = qt2_w)then
	update	lote_orgao_cobranca a
	set	a.dt_aprovacao		= clock_timestamp()
	where	a.nr_sequencia		= nr_seq_lote_p;
end if;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
