SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_ap_lote_suspenso ( nr_seq_lote_p bigint, cd_material_p bigint, qt_total_baixar_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE




qt_total_dispensar_w	double precision;
qt_suspenso_w		double precision;
ds_material_w		varchar(255);
ds_erro_w		varchar(255);
qt_total_w		double precision;


BEGIN

select	coalesce(sum(qt_dispensar), 0)
into STRICT	qt_total_dispensar_w
from	ap_lote_item
where	nr_seq_lote = nr_seq_lote_p
and	cd_material = cd_material_p;

select	coalesce(sum(qt_dispensar), 0)
into STRICT	qt_suspenso_w
from	ap_lote_item
where	nr_seq_lote = nr_seq_lote_p
and	cd_material = cd_material_p
and	(dt_supensao IS NOT NULL AND dt_supensao::text <> '');

qt_total_w	:= ( qt_total_dispensar_w - qt_total_baixar_p);

if	((qt_total_w) >= 0) and (qt_suspenso_w > (qt_total_w)) then
	ds_erro_w	:= WHEB_MENSAGEM_PCK.get_texto(280143);
end if;


ds_erro_p	:= substr(ds_erro_w,1,255);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
