SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_assumir_transporte ( nm_usuario_p text, nr_sequencia_p bigint, ds_retorno_p INOUT text) AS $body$
DECLARE



ie_possui_solicitacao_aberta_w		varchar(1);
ds_mensagem_w				varchar(255)	:=	'';


BEGIN

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_possui_solicitacao_aberta_w
from	trans_solicitacao
where	nr_sequencia = nr_sequencia_p
and	ie_status	not in (8,9)
and	ie_regra_prioridade = 'S';

if (ie_possui_solicitacao_aberta_w = 'S') then
	ds_mensagem_w	:= obter_texto_dic_objeto(197130, wheb_usuario_pck.get_nr_seq_idioma, null);
	--O transportador possuí solicitação de transporte com prioridade pendente.
end if;

ds_retorno_p	:= ds_mensagem_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
