CREATE OR REPLACE PROCEDURE consiste_atend_cirurgia ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, nr_cirurgia_p bigint, ds_mensagem_p INOUT text) AS $body$
DECLARE

 
qt_atendimento_w	bigint;


BEGIN 
select	count(*) 
into STRICT	qt_atendimento_w 
from	atendimento_Paciente 
where	nr_atendimento	= nr_atendimento_p 
and	cd_pessoa_fisica	= cd_pessoa_fisica_p;
 
if (qt_atendimento_w <> 0) then 
	begin 
	update	cirurgia 
	set	nr_atendimento	= nr_atendimento_p, 
		dt_entrada_unidade	= clock_timestamp() 
	where	nr_cirurgia		= nr_cirurgia_p 
	and	nr_atendimento	= nr_atendimento_p 
	and	cd_pessoa_fisica	= cd_pessoa_fisica_p;
	commit;
	end;
else 
	ds_mensagem_p:= Wheb_mensagem_pck.get_texto(306315, null); -- Este atendimento n√£o pertence a este paciente 
end if;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
