CREATE OR REPLACE PROCEDURE consiste_atend_prescr_proc ( cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_estabelecimento_p bigint, ie_permite_p INOUT text) AS $body$
DECLARE


cd_grupo_w				bigint		:= 0;
cd_especialidade_w			bigint		:= 0;
cd_area_w				bigint		:= 0;
ie_permite_w				varchar(1);

C01 CURSOR FOR
	SELECT	ie_permite
	from	regra_atend_prescr_proc a
	where	a.ie_situacao					= 'A'
	and 	a.cd_estabelecimento      			= cd_estabelecimento_p
	and	a.cd_perfil					= obter_Perfil_Ativo
	and	coalesce(a.cd_procedimento,cd_procedimento_p) 	= cd_procedimento_p
	and	((coalesce(cd_procedimento::text, '') = '') or (coalesce(a.ie_origem_proced,ie_origem_proced_p)= ie_origem_proced_p))
	and	coalesce(a.cd_area_procedimento,cd_area_w)		= cd_area_w
	and	coalesce(a.cd_especialidade,cd_especialidade_w)	= cd_especialidade_w
	and	coalesce(a.cd_grupo_proc,cd_grupo_w)			= cd_grupo_w
	order by	coalesce(a.cd_procedimento,0),
			coalesce(a.cd_grupo_proc,0),
			coalesce(a.cd_especialidade,0),
			coalesce(a.cd_area_procedimento,0);



BEGIN


/* Obter Estrutura do procedimento */

begin
select 	cd_grupo_proc,
	cd_especialidade,
	cd_area_procedimento
into STRICT	cd_grupo_w,
	cd_especialidade_w,
	cd_area_w
from		Estrutura_Procedimento_V
where		cd_procedimento 	= cd_procedimento_p
and		ie_origem_proced	= ie_origem_proced_p;
exception
     		when others then
		begin
		cd_grupo_w		:= 0;
		cd_especialidade_w	:= 0;
		cd_area_w		:= 0;
		end;
end;

open C01;
loop
fetch C01 into
	ie_permite_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

ie_permite_p	:= coalesce(ie_permite_w,'S');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
