SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_ativ_real ( NR_SEQ_OS_P bigint, NR_SEQ_ATIV_REAL_P bigint, NR_SEQ_ATIV_PREV_P bigint, NR_SEQ_PROJ_CRON_ETAPA_P bigint, NR_SEQ_FUNCAO_P bigint, CD_SETOR_ATEND_P bigint, IE_EXCLUSIVA_P text, IE_BASE_CORP_P text, IE_EDICAO_P text, NM_USUARIO_P text, IE_CONSISTE_ATIV_P text, nr_seq_os_iniciada_p INOUT bigint, DS_RETORNO_P INOUT text, DS_CONFIRMA_P INOUT text ) AS $body$
DECLARE


cd_estabelecimento_w		smallint;
param_w				varchar(2);
ds_confirma_w			varchar(255);
--param_38_w			varchar2(2);
qt_ativ_prev_w			smallint;
qt_minuto_old_w			integer;
ie_consiste_prioridade_w 	varchar(2);
nr_seq_os_iniciada_w 		man_ordem_servico.nr_sequencia%type;
nr_seq_proj_cron_etapa_w	MAN_ORDEM_SERV_ATIV.nr_seq_proj_cron_etapa%type;
nr_seq_grupo_sup_w		usuario_grupo_sup.nr_seq_grupo%type;
nr_seq_grupo_des_w		usuario_grupo_des.nr_seq_grupo%type;
ie_prioridade_sup_w		smallint;
ie_prioridade_desen_w		smallint;
ie_base_wheb_w			varchar(1);


BEGIN
cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;


param_w := Obter_param_Usuario(297, 36, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, param_w);
if ('S' = param_w)
	and (coalesce(nr_seq_ativ_prev_p::text, '') = '') then

		select  count(*)
		into STRICT	qt_ativ_prev_w
		from 	MAN_ORDEM_ATIV_PREV x
		where 	NR_SEQ_ORDEM_SERV = nr_seq_os_p
		and     x.nm_usuario_prev = nm_usuario_p
		and 	trunc(x.dt_prevista) = trunc(clock_timestamp());

		if (qt_ativ_prev_w > 0) then
			ds_retorno_p := obter_texto_tasy(116977, wheb_usuario_pck.get_nr_seq_idioma);
			goto final;
		end if;
end if;

select  max(nr_seq_grupo)
into STRICT	nr_seq_grupo_sup_w
from    usuario_grupo_sup
where   nm_usuario_grupo = nm_usuario_p;

select	max(nr_seq_grupo) nr
into STRICT	nr_seq_grupo_des_w
from	usuario_grupo_des
where	nm_usuario_grupo = nm_usuario_p;



if (nr_seq_grupo_sup_w IS NOT NULL AND nr_seq_grupo_sup_w::text <> '') and (coalesce(nr_seq_grupo_des_w::text, '') = '') then
	ie_consiste_prioridade_w := 'S';
else
	ie_consiste_prioridade_w := Obter_param_Usuario(297, 38, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ie_consiste_prioridade_w);
end if;


SELECT * FROM man_consiste_inicio_ativ_os(nr_seq_os_p, ie_consiste_prioridade_w, nm_usuario_p, nr_seq_os_iniciada_w, ds_confirma_w) INTO STRICT nr_seq_os_iniciada_w, ds_confirma_w;

select CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
INTO STRICT ie_base_wheb_w
from user_users
where username = 'TASY';

select ie_prioridade_desen, ie_prioridade_sup
into STRICT ie_prioridade_desen_w, ie_prioridade_sup_w
from man_ordem_servico
where nr_sequencia = nr_seq_os_p;

if	((ie_base_corp_p = 'S') or (ie_base_wheb_w = 'S'))
and (nr_seq_grupo_sup_w  = '')
and (nr_seq_grupo_des_w <> '')
and	not(ie_prioridade_desen_w > 0)then
	ds_retorno_p := obter_texto_tasy(121354, wheb_usuario_pck.get_nr_seq_idioma);
	goto final;
end if;

if	((ie_base_corp_p = 'S') or (ie_base_wheb_w = 'S'))
and (nr_seq_grupo_sup_w  <> '')
and (nr_seq_grupo_des_w = '')
and	not(ie_prioridade_sup_w > 0)then
	ds_retorno_p := obter_texto_tasy(121355, wheb_usuario_pck.get_nr_seq_idioma);
	goto final;
end if;

if (ie_edicao_p = 'S') then

	select 	nr_seq_proj_cron_etapa,
		qt_minuto
	into STRICT 	nr_seq_proj_cron_etapa_w,
		qt_minuto_old_w
	from 	MAN_ORDEM_SERV_ATIV
	where 	nr_sequencia = nr_seq_ativ_real_p;

	if (nr_seq_proj_cron_etapa_w IS NOT NULL AND nr_seq_proj_cron_etapa_w::text <> '')
	and (nr_seq_proj_cron_etapa_p <> nr_seq_proj_cron_etapa_w) then
		ds_retorno_p := obter_texto_tasy(117021, wheb_usuario_pck.get_nr_seq_idioma);
		goto final;
	end if;
else
	if (ds_confirma_w IS NOT NULL AND ds_confirma_w::text <> '')
	and (coalesce(nr_seq_ativ_prev_p::text, '') = '')
	and (coalesce(nr_seq_os_iniciada_w::text, '') = '') then
		ds_retorno_p := ds_confirma_w;
		goto final;
	end if;
end if;

if (coalesce(nr_seq_proj_cron_etapa_p::text, '') = '')
and (ie_consiste_ativ_p = 'S')
and (nr_seq_funcao_p in (31,11,551,132))
and (obter_se_ativ_os_ativ_proj(nr_seq_os_p) = 'S') then
	ds_retorno_p := obter_texto_tasy(117025, wheb_usuario_pck.get_nr_seq_idioma);
	goto final;
end if;

if (ie_base_corp_p = 'S') then
	if (nr_seq_proj_cron_etapa_p IS NOT NULL AND nr_seq_proj_cron_etapa_p::text <> '') then

		if (obter_se_etapa_dependente_npi(nr_seq_proj_cron_etapa_p) = 'S') then
			ds_retorno_p := obter_texto_tasy(299120, wheb_usuario_pck.get_nr_seq_idioma);
			goto final;
		end if;

		if (obter_consistencia_area_npi(nr_seq_proj_cron_etapa_p) = 'S') then
			ds_retorno_p := obter_texto_tasy(316396, wheb_usuario_pck.get_nr_seq_idioma);
			goto final;
		end if;
	end if;

	param_w := Obter_param_Usuario(297, 32, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, param_w);
	if (cd_setor_atend_p in (2,7,16))
	and (param_w = 'S')
	and (coalesce(nr_seq_grupo_des_w::text, '') = '')
	and (coalesce(ie_exclusiva_p::text, '') = '') then
		ds_retorno_p := obter_texto_tasy(316397, wheb_usuario_pck.get_nr_seq_idioma);
		goto final;
	end if;
end if;

param_w := Obter_param_Usuario(297, 39, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, param_w);
if (nr_seq_os_iniciada_w IS NOT NULL AND nr_seq_os_iniciada_w::text <> '') and ('N' = param_w) then
	ds_confirma_p := ds_confirma_w;

end if;

<<final>>
nr_seq_os_iniciada_p := nr_seq_os_iniciada_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE consiste_ativ_real ( NR_SEQ_OS_P bigint, NR_SEQ_ATIV_REAL_P bigint, NR_SEQ_ATIV_PREV_P bigint, NR_SEQ_PROJ_CRON_ETAPA_P bigint, NR_SEQ_FUNCAO_P bigint, CD_SETOR_ATEND_P bigint, IE_EXCLUSIVA_P text, IE_BASE_CORP_P text, IE_EDICAO_P text, NM_USUARIO_P text, IE_CONSISTE_ATIV_P text, nr_seq_os_iniciada_p INOUT bigint, DS_RETORNO_P INOUT text, DS_CONFIRMA_P INOUT text ) FROM PUBLIC;

