CREATE OR REPLACE PROCEDURE consiste_auditoria_caixa ( nr_seq_caixa_p bigint, qt_dias_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_ultima_auditoria_w	timestamp;
dt_ultimo_saldo_w	timestamp;
qt_dias_sem_auditoria_w	bigint;
dt_proximo_auditar_w	timestamp;


BEGIN

if (nr_seq_caixa_p IS NOT NULL AND nr_seq_caixa_p::text <> '') and (qt_dias_p >= 0) then
	select	coalesce(max(a.dt_saldo),clock_timestamp())
	into STRICT	dt_ultima_auditoria_w
	from	caixa_saldo_diario a
	where	a.nr_seq_caixa	= nr_seq_caixa_p
	and	(a.dt_auditoria IS NOT NULL AND a.dt_auditoria::text <> '');

	select	count(*)
	into STRICT	qt_dias_sem_auditoria_w
	from	caixa_saldo_diario a
	where	a.nr_seq_caixa	= nr_seq_caixa_p
	and	coalesce(a.dt_auditoria::text, '') = ''
	and	a.dt_saldo > dt_ultima_auditoria_w;

	select	max(a.dt_saldo)
	into STRICT	dt_ultimo_saldo_w
	from	caixa_saldo_diario a
	where	a.nr_seq_caixa = nr_seq_caixa_p;

	select	min(a.dt_saldo)
	into STRICT	dt_proximo_auditar_w
	from	caixa_saldo_diario a
	where	a.nr_seq_caixa = nr_seq_caixa_p
	and	coalesce(a.dt_auditoria::text, '') = ''
	and	a.dt_saldo > dt_ultima_auditoria_w;

	if (coalesce(dt_ultima_auditoria_w::text, '') = '') or (qt_dias_sem_auditoria_w > qt_dias_p) then
		-- O saldo do dia #@DT_SALDO#@ precisa ser auditado para poder abrir um novo saldo di√°rio.
		CALL wheb_mensagem_pck.exibir_mensagem_abort(267128, 'DT_SALDO=' || coalesce(dt_proximo_auditar_w,dt_ultimo_saldo_w));
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
