SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_barras_gerar_nf_trans ( cd_estabelecimento_p bigint, nr_ordem_compra_p bigint, ie_entrada_saida_p text, ie_item_nf_p bigint, ds_retorno_p INOUT text) AS $body$
DECLARE


qt_existe_w	bigint;
cd_material_w	bigint;
nr_item_oci_w	integer;
ds_material_w	varchar(255);
ds_retorno_w	varchar(4000);

c01 CURSOR FOR
SELECT	a.nr_item_oci,
	a.cd_material,
	substr(obter_desc_material(a.cd_material),1,255)
from	ordem_compra_item a,
	ordem_compra_item_entrega b
where	a.nr_ordem_compra = b.nr_ordem_compra
and	a.nr_item_oci = b.nr_item_oci
and	substr(obter_se_leitura_barras(cd_estabelecimento_p, a.cd_material, 146),1,1) = 'S'
and	coalesce(a.qt_material,0) > coalesce(a.qt_material_entregue,0)
and	coalesce(a.dt_reprovacao::text, '') = ''
and	coalesce(b.dt_cancelamento::text, '') = ''
and	a.nr_ordem_compra = nr_ordem_compra_p
and	ie_entrada_saida_p = 'S'
and	ie_item_nf_p = 0
group by	a.nr_item_oci,
	a.cd_material
having	((sum(b.qt_prevista_entrega) - max(obter_qt_oci_trans_nota(a.nr_ordem_compra, a.nr_item_oci,'S'))) > 0)

union all

select	nr_item_oci,
	cd_material,
	substr(obter_desc_material(cd_material),1,255)
from	ordem_compra_item_cb
where	coalesce(ie_status,'CB') = 'CM'
and	substr(obter_se_leitura_barras(cd_estabelecimento_p, cd_material, 146),1,1) = 'S'
and	ie_atende_recebe = 'A'
and	coalesce(nr_seq_nota::text, '') = ''
and	nr_ordem_compra = nr_ordem_compra_p
and	ie_item_nf_p = 1
group by nr_item_oci,
	cd_material
order by	nr_item_oci;


BEGIN
ds_retorno_w := null;

select	count(*)
into STRICT	qt_existe_w
from	regra_leitura_barra_mat
where	cd_estabelecimento	= cd_estabelecimento_p;

if (qt_existe_w > 0) then
	begin
	open c01;
	loop
	fetch c01 into
		nr_item_oci_w,
		cd_material_w,
		ds_material_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		ds_retorno_w := substr(ds_retorno_w || '[' || nr_item_oci_w || '] - ' || cd_material_w || ' - ' || ds_material_w || chr(13) || chr(10),1,4000);
		end;
	end loop;
	close c01;
	end;
end if;

if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
	-- Os itens abaixo não serão gerados na NF pois exigem leitura por barras!
	ds_retorno_w := substr(wheb_mensagem_pck.get_texto(298642,'DS_RETORNO='||ds_retorno_w),1,4000);
end if;

ds_retorno_p := ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE consiste_barras_gerar_nf_trans ( cd_estabelecimento_p bigint, nr_ordem_compra_p bigint, ie_entrada_saida_p text, ie_item_nf_p bigint, ds_retorno_p INOUT text) FROM PUBLIC;

