CREATE OR REPLACE PROCEDURE consiste_caixa_opme_agenda ( nr_seq_agenda_p bigint, nr_seq_tipo_caixa_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE

 
cd_convenio_w		integer;
nr_seq_proc_interno_w	bigint;
ie_consiste_w		varchar(1);
ds_erro_w		varchar(255):=null;
ie_consiste_mat_opme_w	varchar(1);
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;


BEGIN 
 
ie_consiste_mat_opme_w := Obter_Param_Usuario(871, 516, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_consiste_mat_opme_w);
 
if (ie_consiste_mat_opme_w = 'S') then 
	select	max(cd_convenio), 
		max(nr_seq_proc_interno), 
		max(cd_procedimento), 
		max(ie_origem_proced) 
	into STRICT	cd_convenio_w, 
		nr_seq_proc_interno_w, 
		cd_procedimento_w, 
		ie_origem_proced_w 
	from 	agenda_paciente 
	where	nr_sequencia = nr_seq_agenda_p;
 
	if (cd_convenio_w IS NOT NULL AND cd_convenio_w::text <> '') and 
		((nr_seq_proc_interno_w IS NOT NULL AND nr_seq_proc_interno_w::text <> '') or (cd_procedimento_w IS NOT NULL AND cd_procedimento_w::text <> '')) and (obter_tipo_convenio(cd_convenio_w) = 3) then 
		select	coalesce(max('S'),'N') 
		into STRICT	ie_consiste_w 
		from 	opme_caixa_item 
		where	nr_seq_caixa = nr_seq_tipo_caixa_p 
		and	sus_obter_se_mat_compativel(cd_estabelecimento_p,cd_convenio_w,nr_seq_proc_interno_w,cd_procedimento_w,coalesce(ie_origem_proced_w,7),cd_material) = 'N';
		if (ie_consiste_w = 'S') then 
			ds_erro_w := WHEB_MENSAGEM_PCK.get_texto(280305);
		end if;
	end if;
end if;
 
if (ds_erro_w IS NOT NULL AND ds_erro_w::text <> '') then 
	ds_erro_p := ds_erro_w;
end if;
 
end;
$body$
