SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_campos_final_cirurgia ( nr_cirurgia_p bigint, ds_erro_p INOUT text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


ie_sem_tipo_anestesia_w		varchar(1);
ie_sem_pot_contaminacao_w	varchar(1);
ie_sem_asa_w			varchar(1);
cd_tipo_anestesia_w		varchar(10);
cd_tipo_cirurgia_w		bigint;
ie_asa_estado_paciente_w	varchar(3);
ds_erro_w			varchar(2000):=null;
ie_consiste_via_w		varchar(1) := 'N';
ie_consiste_w			varchar(1);
nr_prescricao_w			bigint;


BEGIN

ie_sem_tipo_anestesia_w := obter_param_usuario(900, 341, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_sem_tipo_anestesia_w);
ie_sem_pot_contaminacao_w := obter_param_usuario(900, 343, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_sem_pot_contaminacao_w);
ie_sem_asa_w := obter_param_usuario(900, 344, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_sem_asa_w);
ie_consiste_via_w := obter_param_usuario(900, 500, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_consiste_via_w);

select	max(cd_tipo_anestesia),
	max(cd_tipo_cirurgia),
	max(ie_asa_estado_paciente)
into STRICT	cd_tipo_anestesia_w,
	cd_tipo_cirurgia_w,
	ie_asa_estado_paciente_w
from 	cirurgia
where	nr_cirurgia = nr_cirurgia_p;


if (ie_consiste_via_w = 'F') or (ie_consiste_via_w = 'A') then

	select max(nr_prescricao)
	into STRICT	nr_prescricao_w
	from    cirurgia
	where   nr_cirurgia =  nr_cirurgia_p;

	if (coalesce(nr_prescricao_w,0) > 0) then
		select	coalesce(max('S'),'N')
		into STRICT	ie_consiste_w
		from	prescr_procedimento
		where	nr_prescricao 		= nr_prescricao_w
		and	coalesce(cd_motivo_baixa,0) 	= 0
		and	coalesce(ie_via_acesso::text, '') = '';
	end if;
end if;


if (ie_sem_tipo_anestesia_w = 'N') and (coalesce(cd_tipo_anestesia_w::text, '') = '') then
   ds_erro_w := wheb_mensagem_pck.get_texto(278234);
elsif (ie_sem_pot_contaminacao_w = 'N') and (coalesce(cd_tipo_cirurgia_w::text, '') = '') then
   ds_erro_w := wheb_mensagem_pck.get_texto(278239);
elsif (ie_sem_asa_w = 'N') and (coalesce(ie_asa_estado_paciente_w::text, '') = '') then
   ds_erro_w := wheb_mensagem_pck.get_texto(278244);
elsif ((ie_consiste_via_w = 'F') or (ie_consiste_via_w = 'A')) and (ie_consiste_w = 'S') then
   ds_erro_w := wheb_mensagem_pck.get_texto(278170);
end if;

ds_erro_p := ds_erro_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
