SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_carteira_conv_js ( cd_convenio_p text, cd_usuario_convenio_p text, nr_atendimento_p INOUT text, cd_pessoa_fisica_p text, ds_mensagem_p INOUT text, ds_mensagem_aux_p INOUT text) AS $body$
DECLARE

 
qt_convenio_w		bigint;
nr_atendimento_par_w	bigint;
cd_pes_fisica_w		varchar(255);
ds_mensagem_w		varchar(255);
ds_mensagem_aux_w	varchar(255);
			

BEGIN 
 
ds_mensagem_w := '';
ds_mensagem_aux_w := '';
 
 
 
select 	count(*) 
into STRICT	qt_convenio_w 
from  	atend_categoria_convenio a, 
	atendimento_paciente b 
where 	a.nr_atendimento = b.nr_atendimento 
and  	a.cd_convenio = cd_convenio_p 
and  	a.cd_usuario_convenio = cd_usuario_convenio_p 
and  	a.nr_atendimento   <> nr_atendimento_p 
and  	b.cd_pessoa_fisica  <> cd_pessoa_fisica_p;
 
 
if (qt_convenio_w <> 0)then 
	begin 
	 
	select max(a.nr_atendimento), 
		max(substr(obter_nome_pf(b.cd_pessoa_fisica),1,80)) nm_paciente 
	into STRICT	nr_atendimento_par_w, 
		cd_pes_fisica_w 
	from  atend_categoria_convenio a, 
		atendimento_paciente b 
	where  a.nr_atendimento = b.nr_atendimento 
	and  	a.cd_convenio = cd_convenio_p 
	and  	a.cd_usuario_convenio = cd_usuario_convenio_p 
	and  	a.nr_atendimento <> nr_atendimento_p 
	and  	b.cd_pessoa_fisica <> cd_pessoa_fisica_p;
	 
	ds_mensagem_w		:= wheb_mensagem_pck.get_texto(795869, 
							'NR_ATENDIMENTO_PAR='||nr_atendimento_par_w|| 
							';CD_PES_FISICA='||cd_pes_fisica_w);
							 
	ds_mensagem_aux_w	:= wheb_mensagem_pck.get_texto(795863, 
							'NR_ATENDIMENTO='||nr_atendimento_par_w|| 
							';NM_PACIENTE='||cd_pes_fisica_w);
	 
	end;
end if;
 
ds_mensagem_p := ds_mensagem_w;
ds_mensagem_aux_p := ds_mensagem_aux_w;
nr_atendimento_p := nr_atendimento_par_w;
 
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
