CREATE OR REPLACE PROCEDURE consiste_centro_controle ( cd_estab_p bigint, cd_centro_controle_p text, ds_retorno_p INOUT text) AS $body$
DECLARE


ie_situacao_w					varchar(1);
ds_centro_controle_w				varchar(255);


BEGIN

if (cd_centro_controle_p IS NOT NULL AND cd_centro_controle_p::text <> '') then
	begin
	select	coalesce(max(ie_situacao),'I')
	into STRICT 	ie_situacao_w
	from	centro_controle
	where	cd_centro_controle = cd_centro_controle_p
	and	cd_estabelecimento = cd_estab_p;
	if (ie_situacao_w = 'I') then
		begin
		select	max(ds_centro_controle)
		into STRICT	ds_centro_controle_w
		from	centro_controle
		where	cd_centro_controle = cd_centro_controle_p
		and	cd_estabelecimento	= cd_estab_p;
		ds_retorno_p := Wheb_mensagem_pck.get_texto(306273, 'DS_CENTRO_CONTROLE_W='||ds_centro_controle_w);
		end;
	end if;
	end;
end if;
END;
$body$
