CREATE OR REPLACE PROCEDURE consiste_checagem_pac_cir (nr_cirurgia_p bigint, nr_atendimento_p bigint, ds_erro_p INOUT text, nm_usuario_p text) AS $body$
DECLARE

									 
C01 CURSOR FOR 
	SELECT	a.nr_cirurgia 
	from	cirurgia a 
	where	(((a.nr_cirurgia = nr_cirurgia_p) and (coalesce(nr_atendimento_p,0) = 0)) 
	or	 	((a.nr_atendimento = nr_atendimento_p) and (coalesce(nr_cirurgia_p,0) = 0))) 
	and 	coalesce(a.dt_termino::text, '') = '' 
	order by coalesce(a.dt_inicio_real,a.dt_inicio_prevista) desc;
									
ie_checou_pac_cir_w	varchar(1);
nr_cirurgia_w		bigint;
ds_erro_w			varchar(255);
ds_erro_gravacao_w	varchar(255);
									

BEGIN 
 
ds_erro_w 			:= wheb_mensagem_pck.get_texto(286298);
ds_erro_gravacao_w	:= wheb_mensagem_pck.get_texto(286337)||to_char(nr_atendimento_p)||'.'; 	
 
open C01;
loop 
fetch C01 into	 
	nr_cirurgia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;
 
select	coalesce(max('S'),'N') 
into STRICT	ie_checou_pac_cir_w 
from	atend_hist_checagem_pda 
where	nr_cirurgia = nr_cirurgia_w 
and		ie_processo = 'CA';
 
if (ie_checou_pac_cir_w = 'N') then 
	begin 
	ds_erro_p := ds_erro_w;
	CALL gravar_historico_pda(nr_atendimento_p,'AL',ds_erro_gravacao_w,nr_cirurgia_w,null,nm_usuario_p);
	end;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
