CREATE OR REPLACE PROCEDURE consiste_chegada_paciente ( nr_prescricao_p bigint, nr_seq_paciente_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, cd_pessoa_fisica_p INOUT text, ie_regra_setor_p INOUT text) AS $body$
DECLARE


permite_chegada_sem_prescr_w	varchar(1);


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	begin
	select	max(cd_pessoa_fisica)
	into STRICT	cd_pessoa_fisica_p
	from	prescr_medica
	where	coalesce(nr_atendimento::text, '') = ''
	and	nr_prescricao	= nr_prescricao_p;
	end;
end if;

if (coalesce(cd_pessoa_fisica_p::text, '') = '') then
	begin
	/* Quimioterapia - Parâmetro [106] - Permite gerar chegada do paciente sem prescrição */

	permite_chegada_sem_prescr_w := obter_param_usuario(3130, 106, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, permite_chegada_sem_prescr_w);

	if (permite_chegada_sem_prescr_w = 'S') then
		begin
		select	max(cd_pessoa_fisica)
		into STRICT	cd_pessoa_fisica_p
		from	paciente_setor
		where	nr_seq_paciente = nr_seq_paciente_p;
		end;
	end if;
	end;
end if;

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	begin
	select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
	into STRICT	ie_regra_setor_p
	from	regra_setor_atend_quimio
	where	ie_situacao = 'A';
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
