CREATE OR REPLACE PROCEDURE consiste_conta_itaipu (nr_atendimento_p bigint, cd_convenio_p bigint, nm_paciente_p text, ds_retorno_p INOUT text) AS $body$
DECLARE


cd_usuario_convenio_w	varchar(30);
qt_reg_mat		integer;
qt_reg_pac		integer;
ds_retorno_w		varchar(255);
qt_reg_total_w		integer;
qt_conv_regra_w		integer;


BEGIN

qt_reg_mat:= 0;
qt_reg_pac:= 0;
ds_retorno_w:= '';
qt_reg_total_w:= 0;
qt_conv_regra_w:= 0;

select	count(*)
into STRICT	qt_conv_regra_w
from	regra_validacao_usuario
where	cd_convenio = cd_convenio_p
and 	upper(ds_procedure_validacao) = 'CONSISTE_USUARIO_ITAIPU';

if (qt_conv_regra_w > 0) then

	select 	count(*)
	into STRICT	qt_reg_total_w
	from 	w_usuario_convenio;

	if (qt_reg_total_w > 0) then

		cd_usuario_convenio_w:= OBTER_CODIGO_USUARIO_ATECACO(nr_atendimento_p,cd_convenio_p);

		select 	count(*)
		into STRICT	qt_reg_mat
		from	w_usuario_convenio
		where 	cd_usuario_convenio = cd_usuario_convenio_w;



		if (qt_reg_mat = 0) then
			ds_retorno_w:= wheb_mensagem_pck.get_texto(799648);
		end if;

	end if;

end if;

ds_retorno_p:= ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
