CREATE OR REPLACE PROCEDURE consiste_cot_doc_tit_liquidado ( nr_cot_compra_p bigint, qt_registro_p INOUT bigint) AS $body$
DECLARE

 
qt_registro_w			bigint;
qt_nao_liquidados_w		bigint := 0;
ds_erro_w			varchar(255);


BEGIN 
 
qt_nao_liquidados_w := 0;
 
select	count(*) 
into STRICT	qt_registro_w 
from	cot_doc_importacao 
where	nr_cot_compra = nr_cot_compra_p;
 
if (qt_registro_w > 0) then 
	 
	select	count(*) 
	into STRICT	qt_nao_liquidados_w 
	from	cot_doc_importacao 
	where	nr_cot_compra = nr_cot_compra_p 
	and	coalesce(obter_dados_tit_pagar(nr_titulo_pagar,'S'),'X') <> 'L';
end if;
 
qt_registro_p := qt_nao_liquidados_w;
 
end;
$body$
LANGUAGE PLPGSQL
