CREATE OR REPLACE PROCEDURE consiste_data_emissao_nf ( dt_emissao_p timestamp, qt_dia_mes_passado_p bigint, ds_retorno_p INOUT text) AS $body$
DECLARE



dt_mes_anterior_w	timestamp;
dt_mes_emissao_w	timestamp;
qt_dia_emissao_w	bigint;
ds_retorno_w		varchar(255);


BEGIN

select	trunc(dt_emissao_p,'mm')
into STRICT	dt_mes_emissao_w
;

select	trunc(trunc(clock_timestamp(),'mm') -1,'mm')
into STRICT	dt_mes_anterior_w
;

if (dt_mes_emissao_w = dt_mes_anterior_w) then

	select	substr(dt_emissao_p,1,2)
	into STRICT	qt_dia_emissao_w
	;

	if (qt_dia_emissao_w < qt_dia_mes_passado_p) then
		ds_retorno_w := substr(wheb_mensagem_pck.get_texto(312243,'QT_DIA_MES_PASSADO_P='||qt_dia_mes_passado_p),1,255);
	end if;

elsif (dt_mes_emissao_w < dt_mes_anterior_w) then
	ds_retorno_w := substr(wheb_mensagem_pck.get_texto(312243,'QT_DIA_MES_PASSADO_P='||qt_dia_mes_passado_p),1,255);
end if;

ds_retorno_p := ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
