SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_dev_lote_prescr ( nr_atendimento_p bigint, nr_seq_lote_p bigint, cd_estabelecimento_p bigint, cd_material_p INOUT bigint, nm_usuario_p text) AS $body$
DECLARE

 
dt_entrada_unidade_w		timestamp;
dt_atendimento_w			timestamp;
dt_atend_farmacia_w		timestamp;
cd_unidade_medida_w		varchar(30);
cd_motivo_devolucao_w		varchar(3);
cd_setor_atendimento_w		integer;
cd_material_w			bigint := 0;
cd_local_estoque_w		smallint;
nr_sequencia_prescricao_w		integer;
nr_prescricao_w			bigint;
nr_seq_atendimento_w		bigint;
qt_material_w			double precision;
qt_dias_devolucao_w		integer;

C01 CURSOR FOR 
	SELECT	a.cd_material, 
		a.dt_atendimento, 
		a.cd_unidade_medida, 
		a.nr_sequencia_prescricao, 
		a.dt_entrada_unidade, 
		a.nr_sequencia, 
		a.cd_local_estoque, 
		sum(a.qt_material) 
	FROM material b, material_em_devolucao_v a
LEFT OUTER JOIN tipo_baixa_prescricao c ON (a.cd_motivo_baixa = c.cd_tipo_baixa AND a.ie_prescr_dev = c.ie_prescricao_devolucao)
WHERE a.cd_material = b.cd_material and a.nr_atendimento = nr_atendimento_p and a.nr_prescricao	= nr_prescricao_w and a.nr_seq_lote_ap = nr_seq_lote_p and not exists ( SELECT 1 from item_devolucao_material_pac where nr_seq_lote = nr_seq_lote_p) and a.cd_setor_atendimento = coalesce(cd_setor_atendimento_w, a.cd_setor_atendimento)   and ((a.qt_material < 0) or (a.cd_local_estoque IS NOT NULL AND a.cd_local_estoque::text <> '') or (c.ie_atualiza_estoque = 'N' AND c.ie_conta_paciente = 'S')) GROUP BY 
		a.cd_material, 
		a.dt_atendimento, 
		a.cd_unidade_medida, 
		a.nr_sequencia_prescricao, 
		a.dt_entrada_unidade, 
		a.nr_sequencia, 
		a.cd_local_estoque, 
		a.nr_seq_lote_ap;
					

BEGIN 
cd_material_p	:= 0;
cd_motivo_devolucao_w	:= coalesce(obter_valor_param_usuario(42, 15, Obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p),'0');
if (coalesce(cd_motivo_devolucao_w::text, '') = '' HAVING	sum(a.qt_material) > 0 
	) then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(265951);
	--'Para realizar a devolução é necessário informar o motivo de devolução!' 
	--'Parâmetro [15], função Devolução Material Paciente.' 
end if;
qt_dias_devolucao_w	:= coalesce(obter_valor_param_usuario(7029, 62, Obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p),0);
 
select	coalesce(nr_prescricao,0), 
	coalesce(cd_setor_atendimento,0), 
	dt_atend_farmacia 
into STRICT	nr_prescricao_w, 
	cd_setor_atendimento_w, 
	dt_atend_farmacia_w 
from	ap_lote 
where	nr_sequencia = nr_seq_lote_p;
 
if (coalesce(qt_dias_devolucao_w,0) > 0) then 
	if (not clock_timestamp() between to_char(dt_atend_farmacia_w,'dd/mm/yyyy') and to_char((dt_atend_farmacia_w + qt_dias_devolucao_w),'dd/mm/yyyy')) then 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(266369);
		--'Superada quantidade de dias para devolução! Parâmetro [62].'); 
	end if;
end if;
 
open C01;
loop 
fetch C01 into	 
	cd_material_w, 
	dt_atendimento_w, 
	cd_unidade_medida_w, 
	nr_sequencia_prescricao_w, 
	dt_entrada_unidade_w, 
	nr_seq_atendimento_w, 
	cd_local_estoque_w, 
	qt_material_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	if (cd_material_w > 0) then 
		if (coalesce(dt_entrada_unidade_w::text, '') = '') then 
			CALL wheb_mensagem_pck.exibir_mensagem_abort(266370);
			--'Paciente sem data de entrada no setor!'); 
		end if;
	end if;
	end;
end loop;
close C01;
 
cd_material_p := cd_material_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE consiste_dev_lote_prescr ( nr_atendimento_p bigint, nr_seq_lote_p bigint, cd_estabelecimento_p bigint, cd_material_p INOUT bigint, nm_usuario_p text) FROM PUBLIC;

