SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_dispensacao_pepo ( nr_cirurgia_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_consiste_w		varchar(1);
ie_prescr_atend_w	varchar(1);
cd_perfil_w		integer;


BEGIN

cd_perfil_w		:=	obter_perfil_ativo;

ie_prescr_atend_w := Obter_Param_Usuario(872, 503, cd_perfil_w, nm_usuario_p, cd_estabelecimento_p, ie_prescr_atend_w);

if (ie_prescr_atend_w = 'N') then
	select	coalesce(max('S'),'N')
	into STRICT	ie_consiste_w
	from	cirurgia a,
		prescr_material b,
		material_atend_paciente c
	where	a.nr_prescricao = b.nr_prescricao
	and	b.nr_prescricao = c.nr_prescricao
	and	b.nr_sequencia  = c.NR_SEQUENCIA_PRESCRICAO
	and	coalesce(c.cd_motivo_exc_conta::text, '') = ''
	and	a.nr_cirurgia	= nr_cirurgia_p;

	if (ie_consiste_w = 'S') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(271506);
	end if;
end if;



end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
