SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_dose_calculada (nr_seq_paciente_p bigint, nm_usuario_p text, ds_consistencia_p INOUT text) AS $body$
DECLARE


ie_exige_calculo_w		varchar(1)	:= 'N';
cd_estabelecimento_w  	bigint 		:= 0;
qt_registro_w		bigint		 := 0;


BEGIN
cd_estabelecimento_w 	:= obter_estabelecimento_ativo;

ie_exige_calculo_w := Obter_Param_Usuario(281, 443, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ie_exige_calculo_w);

if (ie_exige_calculo_w = 'S') then
	select	count(*)
	into STRICT	qt_registro_w
	from	paciente_protocolo_medic
	where	nr_seq_paciente	= nr_seq_paciente_p
	and	((coalesce(cd_unid_med_prescr::text, '') = '') or (coalesce(qt_dose_prescr::text, '') = ''));

	if (qt_registro_w > 0) then
		ds_consistencia_p	:= ds_consistencia_p || obter_texto_tasy(405599, wheb_usuario_pck.get_nr_seq_idioma) || chr(13);
	end if;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
