CREATE OR REPLACE PROCEDURE consiste_dose_operacao ( ds_dose_diferenciada_p text, cd_intervalo_p text, ds_retorno_p INOUT text) AS $body$
DECLARE


ds_retorno_w			varchar(1)	:= 'S';
qt_operacao_w			bigint;
ds_intervalos_w			varchar(20) 	:= ',-';
qt_numeros_w			bigint	:= 0;
i				integer;
x				varchar(1);
y				varchar(1);
Z				varchar(255);

BEGIN

if (ds_dose_diferenciada_p IS NOT NULL AND ds_dose_diferenciada_p::text <> '') and (cd_intervalo_p IS NOT NULL AND cd_intervalo_p::text <> '') then

	select 	coalesce(max(a.qt_operacao),1)
	into STRICT	qt_operacao_w
	from	intervalo_prescricao a
	where  	a.cd_intervalo = cd_intervalo_p;

	for i in 1..length(ds_dose_diferenciada_p) loop
		begin
		x	:= substr(ds_dose_diferenciada_p, i, 1);
		if (position(x in ds_intervalos_w) > 0) then
			y	:= substr(ds_dose_diferenciada_p, (i - 1), 1);
			z	:= substr(ds_dose_diferenciada_p, (i + 1), length(ds_dose_diferenciada_p));

			if ( i > 1) and (position(y in ds_intervalos_w) = 0) then
				qt_numeros_w := qt_numeros_w + 1;
			end if;

			if (position(',' in z) = 0) and (position('-' in z) = 0) and (somente_numero(z) > 0 )then
				qt_numeros_w := qt_numeros_w + 1;
			end if;
		end if;
		end;
	end loop;

	if (qt_numeros_w = 0) then
		if (somente_numero(ds_dose_diferenciada_p) > 0) then
			qt_numeros_w := 1;
		end if;
	end if;

	if (qt_numeros_w <> qt_operacao_w) then

		ds_retorno_w := 'N';

	end if;
end if;

ds_retorno_p :=	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
