CREATE OR REPLACE PROCEDURE consiste_duplicar_sae (nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


C01 CURSOR FOR
	SELECT	a.nr_seq_diag, round((clock_timestamp() - a.dt_atualizacao)::numeric,5) qt_dias_passadas, qt_horas_reaval_noc / 24
	from	pe_prescr_diag a
	where	a.nr_seq_prescr = nr_sequencia_p
	and	 	(qt_horas_reaval_noc IS NOT NULL AND qt_horas_reaval_noc::text <> '')
	and 	obter_se_consistir_regra_sae(11,OBTER_PERFIL_ATIVO) = 'D';

qt_horas_reaval_noc_w	bigint;
qt_dias_passados_w		double precision;
nr_seq_diag_w			bigint;
nr_seq_erro_w			bigint;


BEGIN

delete	FROM pe_prescricao_erro
where	nr_seq_prescr	= nr_sequencia_p
and		(NR_SEQ_DIAG IS NOT NULL AND NR_SEQ_DIAG::text <> '');

open C01;
loop
fetch C01 into
	nr_seq_diag_w,
	qt_dias_passados_w,
	qt_horas_reaval_noc_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (qt_horas_reaval_noc_w < qt_dias_passados_w) then
		nr_seq_erro_w := gerar_erro_interv_sae(nr_sequencia_p, null, 11, 'N', null, null, nm_usuario_p, nr_seq_erro_w, null, nr_seq_diag_w);
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
