CREATE OR REPLACE PROCEDURE consiste_duplic_empresa_ref ( cd_empresa_p bigint, nm_empresa_p text, ie_comercial_ops_p text, cd_cnpj_p text, ds_erro_p INOUT text) AS $body$
DECLARE




qt_existe_w		bigint;
ds_erro_w		varchar(255)	:= '';

BEGIN

if (nm_empresa_p IS NOT NULL AND nm_empresa_p::text <> '') then
	begin
	select 	count(*)
	into STRICT	qt_existe_w
	from	empresa_referencia
	where	((cd_cgc = cd_cnpj_p AND cd_cgc IS NOT NULL AND cd_cgc::text <> '') or
		((UPPER(nm_empresa) = UPPER(nm_empresa_p)) and (coalesce(cd_cgc::text, '') = '')));
	if (qt_existe_w > 0) then
		begin
		ds_erro_w	:= WHEB_MENSAGEM_PCK.get_texto(279687,null);
		end;
	end if;
	end;
end if;
ds_erro_p	:=	ds_erro_w;

commit;

end;
$body$
