SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_empresa_bloqueio ( cd_empresa_p bigint, cd_convenio_p bigint, cd_categoria_p text, ds_erro_p INOUT text) AS $body$
DECLARE


ds_erro_w			varchar(255);
cd_empresa_w			bigint;
qt_inativa_w			integer;


BEGIN

select 	count(*)
into STRICT	qt_inativa_w
from 	EMPRESA_REFERENCIA
where	cd_empresa = cd_empresa_p
and	ie_situacao = 'I';

select	coalesce(max(cd_empresa),0)
into STRICT	cd_empresa_w
from	CONVENIO_EMPRESA_BLOQUEIO
where	cd_convenio		= cd_convenio_p
and	cd_categoria		= cd_categoria_p
and	cd_empresa		= cd_empresa_p;

if (qt_inativa_w > 0) then
	ds_erro_w	:= WHEB_MENSAGEM_PCK.get_texto(279385,null);
end if;

if (cd_empresa_w	<> 0) then
	begin
	if (qt_inativa_w > 0) then
		ds_erro_w	:=	ds_erro_w || chr(13);
	end if;

	ds_erro_w	:= ds_erro_w || WHEB_MENSAGEM_PCK.get_texto(279387,null);
	end;
end if;

ds_erro_p	:= ds_erro_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
