SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_escala_espast ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text, ie_ashworth_p INOUT text) AS $body$
DECLARE

 
qt_reg_w	bigint;

 

BEGIN 
 
 
select	count(*) 
into STRICT	qt_reg_w 
from	ESPASTICIDADE_AVALIACAO 
where	IE_EXIBE_ASHWORTH = 'S';
 
if (qt_reg_w	> 0) then 
 
	select	count(*) 
	into STRICT	qt_reg_w 
	from	med_avaliacao_paciente a 
	where	a.nr_atendimento = nr_atendimento_p 
	and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') 
	and		coalesce(a.dt_inativacao::text, '') = '' 
	and		exists (	SELECT	1 
						from	ESPASTICIDADE_AVALIACAO x 
						where	x.NR_SEQ_TIPO_AVALIACAO = a.NR_SEQ_TIPO_AVALIACAO 
						and		x.IE_EXIBE_ASHWORTH = 'S');
						 
	if (qt_reg_w	= 0) then 
		ie_ashworth_p	:= 'N';
	else 
		ie_ashworth_p	:= 'S';
	end if;
	 
else 
	 
	ie_ashworth_p	:= 'S';
 
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
