CREATE OR REPLACE PROCEDURE consiste_evento_pepo (nr_cirurgia_p bigint, nr_seq_pepo_p bigint, nr_seq_evento_p bigint, dt_evento_p timestamp, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE


