	if ((ie_etapa_cirurgia_w = 1) and ((dt_evento_p > dt_registro_ini_anst_w) or (dt_evento_p > dt_registro_ini_proc_w) or (dt_evento_p > dt_registro_proc_w) or (dt_evento_p > dt_registro_anst_w) or (dt_evento_p > dt_registro_cir_w))) then
