CREATE OR REPLACE PROCEDURE consiste_falta_tipo_doc_pj ( cd_cnpj_p text, ds_lista_p INOUT text, nm_usuario_p text) AS $body$
DECLARE


cd_tipo_pessoa_w				smallint;
nr_seq_tipo_doc_fornec_w			bigint;
ds_tipo_documento_w			varchar(255);
ds_lista_documentos_w			varchar(255) := '';

c01 CURSOR FOR
SELECT	cd_tipo_pessoa
from	pessoa_juridica
where	cd_cgc = cd_cnpj_p

union

select	cd_tipo_pessoa
from	pessoa_jur_tipo_adic
where	cd_cgc = cd_cnpj_p;

c02 CURSOR FOR
SELECT	a.nr_seq_tipo_doc_fornec
from	tipo_pessoa_documento a
where	a.cd_tipo_pessoa = cd_tipo_pessoa_w
and not exists (
	SELECT	1
	from	pessoa_juridica_doc b
	where	a.nr_seq_tipo_doc_fornec = b.nr_seq_tipo_docto
	and	b.cd_cgc = cd_cnpj_p);

BEGIN

open C01;
loop
fetch C01 into
	cd_tipo_pessoa_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	open C02;
	loop
	fetch C02 into
		nr_seq_tipo_doc_fornec_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		if (obter_se_contido(nr_seq_tipo_doc_fornec_w,ds_lista_documentos_w) = 'N') then
			ds_lista_documentos_w := substr(ds_lista_documentos_w || nr_seq_tipo_doc_fornec_w || ',',1,255);
		end if;

		end;
	end loop;
	close C02;
	end;
end loop;
close C01;

ds_lista_p := substr(ds_lista_documentos_w,1,length(ds_lista_documentos_w) -1);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
