SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_gerar_atend_agenda (nr_seq_agenda_p bigint, ds_consistencia_p INOUT text) AS $body$
DECLARE


cd_pessoa_fisica_w	varchar(10);
dt_nascimento_w		timestamp;
ie_sexo_w		varchar(01);


BEGIN

select	coalesce(max(cd_pessoa_fisica),'0')
into STRICT	cd_pessoa_fisica_w
from 	agenda_paciente
where	nr_sequencia	= nr_seq_agenda_p;

if (cd_pessoa_fisica_w <> '0') then
	begin


	select	max(dt_nascimento),
		max(ie_sexo)
	into STRICT	dt_nascimento_w,
		ie_sexo_w
	from	pessoa_fisica
	where	cd_pessoa_fisica	= cd_pessoa_fisica_w;

	if (coalesce(dt_nascimento_w::text, '') = '') then
		ds_consistencia_p	:= ds_consistencia_p || wheb_mensagem_pck.get_texto(796354) || ' ' || chr(13) || chr(10);
	end if;

	if (coalesce(ie_sexo_w::text, '') = '') then
		ds_consistencia_p	:= ds_consistencia_p || wheb_mensagem_pck.get_texto(796355) || ' ' || chr(13) || chr(10);
	end if;

	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
