SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_gerar_proc_cirurgia (nr_cirurgia_p bigint, nr_prescricao_p bigint, qtd_proc_import_p INOUT text) AS $body$
DECLARE


cd_estabelecimento_w				integer;
cd_perfil_w							integer;
nm_usuario_w						varchar(15);

ie_perm_real_proc_sem_fim_w			varchar(15);
ie_consiste_proc_autorizado_w		varchar(15);

dt_termino_w						timestamp;
ie_carater_cirurgia_w				varchar(15);
ie_proc_autorizado_w				varchar(2);
ds_erro_w							varchar(250);


BEGIN

cd_estabelecimento_w	:= wheb_usuario_pck.get_cd_estabelecimento;
cd_perfil_w				:= wheb_usuario_pck.get_cd_perfil;
nm_usuario_w			:= wheb_usuario_pck.get_nm_usuario;

ie_perm_real_proc_sem_fim_w := Obter_Param_Usuario(900, 420, cd_perfil_w, nm_usuario_w, cd_estabelecimento_w, ie_perm_real_proc_sem_fim_w);
ie_consiste_proc_autorizado_w := Obter_Param_Usuario(900, 239, cd_perfil_w, nm_usuario_w, cd_estabelecimento_w, ie_consiste_proc_autorizado_w);

select 	max(dt_termino), max(ie_carater_cirurgia)
into STRICT 	dt_termino_w, ie_carater_cirurgia_w
from 	cirurgia
where nr_cirurgia = nr_cirurgia_p;

if (ie_perm_real_proc_sem_fim_w = 'N') and (coalesce(dt_termino_w::text, '') = '') then
	--É necessário finalizar a cirurgia para realizar todos os procedimentos previstos
	CALL exibir_erro_abortar(obter_texto_tasy(356449, wheb_usuario_pck.get_nr_seq_idioma),null);
end if;

if (ie_consiste_proc_autorizado_w = 'S') then

	select max(obter_se_cod_proc_igual(nr_cirurgia_p))
	into STRICT ie_proc_autorizado_w
	;

	if (ie_proc_autorizado_w = 'N') then
		--Procedimento principal não está autorizado
		CALL exibir_erro_abortar(obter_texto_tasy(54358, wheb_usuario_pck.get_nr_seq_idioma),null);
	end if;

end if;

if (ie_consiste_proc_autorizado_w = 'E') and (ie_carater_cirurgia_w = 'E') then
	select max(obter_se_cod_proc_igual(nr_cirurgia_p))
	into STRICT ie_proc_autorizado_w
	;

	if (ie_proc_autorizado_w = 'N') then
		--Procedimento principal não está autorizado
		CALL exibir_erro_abortar(obter_texto_tasy(54358, wheb_usuario_pck.get_nr_seq_idioma),null);
	end if;

end if;

ds_erro_w := consistir_proc_lanc_conta(nr_prescricao_p, ds_erro_w, nm_usuario_w, cd_estabelecimento_w);

if (ds_erro_w IS NOT NULL AND ds_erro_w::text <> '') then
	CALL exibir_erro_abortar(ds_erro_w, null);
end if;

select 	max(obter_quantidade_proc_import(nr_cirurgia_p))
into STRICT 	qtd_proc_import_p
;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
