SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_grupo_centro_conta ( cd_conta_contabil_p text, cd_centro_custo_p bigint, ie_tipo_p text, ds_erro_p INOUT text) AS $body$
DECLARE



ie_tipo_grupo_centro_w	varchar(1);
ie_tipo_grupo_conta_w	varchar(1);
ds_erro_w		varchar(255);


BEGIN
if (cd_conta_contabil_p IS NOT NULL AND cd_conta_contabil_p::text <> '') and (cd_centro_custo_p IS NOT NULL AND cd_centro_custo_p::text <> '') then

	select	coalesce(max(a.ie_tipo),'X')
	into STRICT	ie_tipo_grupo_centro_w
	from	centro_custo b,
		ctb_grupo_centro a
	where	a.cd_grupo = b.cd_grupo
	and	b.cd_centro_custo = cd_centro_custo_p;

	select	coalesce(max(a.ie_tipo),'X')
	into STRICT	ie_tipo_grupo_conta_w
	from	conta_contabil b,
		ctb_grupo_conta a
	where	a.cd_grupo = b.cd_grupo
	and	b.cd_conta_contabil = cd_conta_contabil_p;

	if (ie_tipo_grupo_centro_w	<> 'X') and (ie_tipo_grupo_conta_w	<> 'X') and (ie_tipo_grupo_centro_w	<> ie_tipo_grupo_conta_w) then
		ds_erro_w	:= WHEB_MENSAGEM_PCK.get_texto(279355) ||
				chr(13) || chr(10) || WHEB_MENSAGEM_PCK.get_texto(279356) || cd_conta_contabil_p || ' - ' || ie_tipo_grupo_conta_w ||
				chr(13) || chr(10) || WHEB_MENSAGEM_PCK.get_texto(279357) || cd_centro_custo_p || ' - ' || ie_tipo_grupo_centro_w;
	end if;

end if;

ds_erro_p	:= ds_erro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
