CREATE OR REPLACE PROCEDURE consiste_grupo_quim_atc ( cd_grupo_quimico_p text, ds_grupo_quimico_p text, cd_subcla_terap_p text, nm_usuario_p text) AS $body$
DECLARE


qt_registros_w		bigint;


BEGIN

begin
select	count(*)
into STRICT	qt_registros_w
from	med_grupo_quim_farm
where	cd_grupo_quimico	= cd_grupo_quimico_p;
exception
	when others then
	qt_registros_w	:= 0;
end;

if (qt_registros_w = 0) then
	insert into med_grupo_quim_farm(
		cd_grupo_quimico,
		dt_atualizacao,
		nm_usuario,
		ds_grupo_quimico,
		cd_subclasse_terapeutica,
		dt_atualizacao_nrec,
		nm_usuario_nrec
	) values (
		cd_grupo_quimico_p,
		clock_timestamp(),
		nm_usuario_p,
		ds_grupo_quimico_p,
		cd_subcla_terap_p,
		clock_timestamp(),
		nm_usuario_p
	);
else
	update	med_grupo_quim_farm
	set	ds_grupo_quimico	= ds_grupo_quimico_p,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	cd_grupo_quimico	= cd_grupo_quimico_p;
end if;

commit;

end;
$body$
