CREATE OR REPLACE PROCEDURE consiste_guia_senha_prescr_lib ( nr_prescricao_p bigint, ie_consiste_guia_senha_p text, ds_mensagem_p INOUT text) AS $body$
DECLARE


nr_doc_conv_w	varchar(20);
cd_senha_w	varchar(20);


BEGIN

ds_mensagem_p := '';

if (coalesce(nr_prescricao_p,0) > 0) then

	select	max(nr_doc_conv),
		max(cd_senha)
	into STRICT	nr_doc_conv_w,
		cd_senha_w
	from	prescr_medica
	where	nr_prescricao = nr_prescricao_p;


	if (ie_consiste_guia_senha_p = 'G') and (coalesce(nr_doc_conv_w::text, '') = '') then

		ds_mensagem_p := wheb_mensagem_pck.get_texto(796393);

	elsif (ie_consiste_guia_senha_p = 'S') and (coalesce(cd_senha_w::text, '') = '') then

		ds_mensagem_p := wheb_mensagem_pck.get_texto(796448);

	elsif (ie_consiste_guia_senha_p = 'SG') and
		((coalesce(nr_doc_conv_w::text, '') = '') or (coalesce(cd_senha_w::text, '') = '')) then

		 ds_mensagem_p := wheb_mensagem_pck.get_texto(796449);

	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
