CREATE OR REPLACE PROCEDURE consiste_horario_escala ( nr_seq_escala_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, nm_usuario_p text, ds_consistencia_p INOUT text) AS $body$
DECLARE


qt_escala_w    bigint;
dt_inicial_w   timestamp;
dt_final_w     timestamp;


BEGIN

dt_inicial_w	:= trunc(dt_inicio_p,'dd');
dt_final_w		:= fim_dia(dt_fim_p);


select	count(*)
into STRICT	qt_escala_w
from	escala_diaria
where	dt_inicio between dt_inicial_w and dt_final_w
and	nr_seq_escala = nr_seq_escala_p;

if (qt_escala_w > 0) then
	ds_consistencia_p := wheb_mensagem_pck.get_texto(760593); -- 'Existem horários gerados para este período. Deseja sobrescrevê-los?';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
