SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_imped_medico_atend (cd_medico_resp_p text, ie_tipo_atendimento_p bigint) AS $body$
DECLARE


qt_impedimento_w	bigint;
qt_atend_internado_w	bigint;
qt_impedimento_ww	varchar(3);
qt_atend_intern_w	varchar(3);
qt_atend_interno_perm_w	varchar(3);


BEGIN

if (coalesce(ie_tipo_atendimento_p,0) > 0) and (cd_medico_resp_p IS NOT NULL AND cd_medico_resp_p::text <> '') then
	begin
	select	count(*)
	into STRICT	qt_impedimento_w
	from	pf_impedimento
	where	cd_pessoa_fisica = cd_medico_resp_p
	and	ie_impedimento = 'EUPI'
	and	trunc(clock_timestamp()) between dt_inicio and coalesce(dt_final,clock_timestamp() + interval '1 days');

	if (qt_impedimento_w > 0) then
		begin
		select	count(*)
		into STRICT	qt_atend_internado_w
		from	atendimento_paciente
		where	cd_medico_resp = cd_medico_resp_p
		and  	ie_tipo_atendimento = 1
		and	coalesce(dt_alta::text, '') = '';

		if (qt_atend_internado_w > 0) and (ie_tipo_atendimento_p = 1) then

			CALL Wheb_mensagem_pck.exibir_mensagem_abort(197003);
			--'O médico não pode ser responsável ao mesmo tempo de dois atendimentos do tipo Internado. Verificar os impedimentos do cadastro médico.';
		end if;
		end;
	end if;

	select count(*)
	into STRICT	qt_impedimento_ww
	from	pf_impedimento
	where	cd_pessoa_fisica =	cd_medico_resp_p
	and	ie_impedimento = 'EUPQ'
	and	trunc(clock_timestamp()) between dt_inicio and coalesce(dt_final,clock_timestamp() + interval '1 days');

	if (qt_impedimento_ww > 0) then
		begin
		select	sum(coalesce(qt_atend_internado,0))
		into STRICT	qt_atend_interno_perm_w
		from	pf_impedimento
		where	cd_pessoa_fisica = cd_medico_resp_p
		and	ie_impedimento = 'EUPQ'
		and	trunc(clock_timestamp()) between dt_inicio and coalesce(dt_final,clock_timestamp() + interval '1 days');

		select	count(*)
		into STRICT	qt_atend_intern_w
		from	atendimento_paciente
		where	cd_medico_resp = cd_medico_resp_p
		and  	ie_tipo_atendimento = 1
		and	coalesce(dt_alta::text, '') = '';

		if (qt_atend_intern_w > qt_atend_interno_perm_w) then

			CALL Wheb_mensagem_pck.exibir_mensagem_abort(247939);
			--'O médico não pode ser responsável pois já superou a quantidade de atendimentos do tipo Internado. Verificar os impedimentos do cadastro médico.';
		end if;
		end;
	end if;


	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
