CREATE OR REPLACE PROCEDURE consiste_item_prescricao ( nr_prescricao_p text, nr_sequencias_p text, ie_opcao_p text, cd_perfil_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE

 
					 
nr_sequencias_w		varchar(2000);
nr_sequencia_w		bigint;
nr_pos_virgula_w	bigint;
ds_erro_w		varchar(255);
					

BEGIN 
 
if (nr_sequencias_p IS NOT NULL AND nr_sequencias_p::text <> '') and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then 
	begin 
	nr_sequencias_w	:= nr_sequencias_p;
	while(nr_sequencias_w IS NOT NULL AND nr_sequencias_w::text <> '') loop 
		begin 
		nr_pos_virgula_w	:= position(',' in nr_sequencias_w);
		if (nr_pos_virgula_w > 0) then 
			begin 
			nr_sequencia_w	:= (substr(nr_sequencias_w,1,nr_pos_virgula_w-1))::numeric;
			nr_sequencias_w	:= substr(nr_sequencias_w,nr_pos_virgula_w+1,length(nr_sequencias_w));
			if (nr_sequencia_w > 0) then 
				begin 
					if ('M' = ie_opcao_p)	then 
						CALL atualiza_antimicrobiano_dia(nr_prescricao_p, nr_sequencia_w);
					elsif ('P' = ie_opcao_p) then 
						ds_erro_w  := consistir_prescr_solucao(nr_prescricao_p, nr_sequencia_w, cd_estabelecimento_p, cd_perfil_p, nm_usuario_p, ds_erro_w );
					else 
						ds_erro_w  := consistir_prescr_material(nr_prescricao_p, nr_sequencia_w, nm_usuario_p, cd_perfil_p, ds_erro_w );
					end if;
				end;
			end if;
			end;
		else 
			begin 
			nr_sequencias_w	:= null;
			end;
		end if;
		end;
	end loop;
	end;
end if;
 
ds_erro_p := ds_erro_w;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
