CREATE OR REPLACE PROCEDURE consiste_item_transf_pend ( cd_material_p bigint, cd_local_estoque_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE

qt_existe_w	bigint;

BEGIN
ds_erro_p := null;
 
select	count(*) 
into STRICT	qt_existe_w 
from	sup_pendencia_transf_estab a, 
	ordem_compra o, 
	ordem_compra_item b 
where	a.nr_ordem_compra = b.nr_ordem_compra 
and	b.nr_ordem_compra = o.nr_ordem_compra 
and	a.nr_item_oci = b.nr_item_oci 
and	b.cd_material = cd_material_p 
and	coalesce(b.cd_local_estoque, o.cd_local_entrega) = cd_local_estoque_p 
and 	not exists (SELECT	1 
		from	ordem_compra_item x 
		where	a.nr_sequencia = x.nr_seq_pendencia_transf);
		 
if (qt_existe_w > 0) then 
	ds_erro_p := WHEB_MENSAGEM_PCK.get_texto(279653);
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
