CREATE OR REPLACE PROCEDURE consiste_kit_estoque_cir ( nr_seq_kit_estoque_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


qt_horas_montagem_w	bigint;
cd_perfil_w		integer;
ie_consiste_w		varchar(1);


BEGIN
if (coalesce(nr_seq_kit_estoque_p,0) > 0) then
	cd_perfil_w	:= obter_perfil_ativo;
	qt_horas_montagem_w := Obter_Param_Usuario(900, 537, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, qt_horas_montagem_w);
	if (qt_horas_montagem_w > 0) then
		select	coalesce(max('S'),'N')
		into STRICT	ie_consiste_w
		from	kit_estoque
		where	nr_sequencia = nr_seq_kit_estoque_p
		and	(coalesce(dt_montagem,clock_timestamp()) + (qt_horas_montagem_w/24)) < clock_timestamp();

		if (ie_consiste_w = 'S') then
			CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(269250);
		end if;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
