CREATE OR REPLACE PROCEDURE consiste_lab_convenio_js ( cd_convenio_p bigint, nr_atendimento_cat_p bigint, cd_estabelecimento_p bigint, ie_obter_se_exige_cpf_p text, nr_atendimento_p bigint, ie_exige_cpf_paciente_p INOUT text, nr_seq_interno_p INOUT bigint, ds_msg_erro_p INOUT text) AS $body$
DECLARE

 
ie_exige_cpf_paciente_w	varchar(1);
ds_msg_erro_w		varchar(255);
ie_continua_exec_w	varchar(1)	:= 'S';
nr_seq_interno_w	bigint;
				

BEGIN 
 
if (cd_convenio_p IS NOT NULL AND cd_convenio_p::text <> '')and (nr_atendimento_cat_p IS NOT NULL AND nr_atendimento_cat_p::text <> '')then 
	begin 
	 
	select 	max(ie_exige_cpf_paciente) 
	into STRICT	ie_exige_cpf_paciente_w 
	from  	convenio_estabelecimento 
	where 	cd_convenio = cd_convenio_p 
	and  	cd_estabelecimento = cd_estabelecimento_p;
	 
	if (ie_obter_se_exige_cpf_p = 'S')and (ie_exige_cpf_paciente_w = 'S')then 
		begin 
		 
		update 	atendimento_paciente 
		set 	ie_tipo_convenio = obter_tipo_convenio(cd_convenio_p) 
		where 	nr_atendimento 	= nr_atendimento_cat_p;
		 
		ds_msg_erro_w		:= Wheb_mensagem_pck.get_texto(306379); -- 'Este convênio exige a informação do CPF do paciente ou responsável. Parâmetro[501].'; 
		ie_continua_exec_w	:= 'N';
		 
		end;
	end if;
	 
	end;
end if;
 
if (ie_continua_exec_w = 'S')and (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '')then 
	begin 
	 
	select 	coalesce(max(nr_seq_interno),0) 
	into STRICT	nr_seq_interno_w 
	from 	atend_categoria_convenio  
	where 	nr_atendimento = nr_atendimento_p;
	 
	if (nr_seq_interno_w = 0)then 
		begin 
		 
		ds_msg_erro_w		:= Wheb_mensagem_pck.get_texto(306380); -- 'É necessário a informação dos dados do convênio!'; 
		
		end;
	end if;
	 
	end;
end if;
 
ds_msg_erro_p		:= ds_msg_erro_w;
nr_seq_interno_p	:= nr_seq_interno_w;
ie_exige_cpf_paciente_p := ie_exige_cpf_paciente_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
