SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_liberacao_escala ( ie_escala_p bigint) AS $body$
DECLARE


qt_reg_w	bigint;
cd_cgc_w	varchar(14);


BEGIN

select	count(*)
into STRICT	qt_reg_w
from	ESCALA_LIBERACAO
where	ie_escala	= ie_escala_p;

if (qt_reg_w	> 0) then

	cd_cgc_w	:= obter_cgc_estabelecimento(wheb_usuario_pck.get_cd_estabelecimento);
	
		select sum(qt_liberacao)
		into STRICT	qt_reg_w	
        from (
                SELECT	coalesce(max(1), 0) qt_liberacao
                from	escala_liberacao
                where	IE_ESCALA	= ie_escala_p
                and		CD_CGC_CLIENTE = cd_cgc_w
                and     coalesce(IE_LIBERACAO_FILIAL,'S') = 'S'
                
union all
 
                SELECT	coalesce(max(1), 0) qt_liberacao
                from	escala_liberacao
                where	IE_ESCALA	= ie_escala_p
                and     coalesce(IE_LIBERACAO_FILIAL,'S') = 'N'              
                and		substr(CD_CGC_CLIENTE,1, 8) = substr(cd_cgc_w,1,8)
                ) x;
				
	if (qt_reg_w	= 0) then
		-- Esta escala necessita de uma autorizacao por escrito do autor da escala para permitir sua liberacao.
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(263515);
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE consiste_liberacao_escala ( ie_escala_p bigint) FROM PUBLIC;

