SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_lib_solic_kit (nr_sequencia_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE


cd_material_w			integer;
qt_disponivel_w			double precision;
cd_local_estoque_w		smallint;
cd_estabelecimento_w		smallint;
qt_solicitada_w			bigint;
ds_erro_w			varchar(1000);
qt_material_requisicao_w		double precision;

C01 CURSOR FOR
SELECT	a.cd_material,
	a.qt_solicitada,
	a.qt_disponivel
from	solic_kit_mat_comp a
where	nr_seq_solic_kit = nr_sequencia_p
and	coalesce(qt_disponivel,0) < qt_solicitada;


BEGIN

select	cd_estabelecimento,
	cd_local_estoque
into STRICT	cd_estabelecimento_w,
	cd_local_estoque_w
from	solic_kit_material
where	nr_sequencia = nr_sequencia_p;

open C01;
loop
fetch C01 into
	cd_material_w,
	qt_solicitada_w,
	qt_disponivel_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	coalesce(sum(qt_material_requisitada - coalesce(qt_material_atendida,0)),0)
	into STRICT	qt_material_requisicao_w
	from	requisicao_material a,
		item_requisicao_material b
	where	a.nr_requisicao = b.nr_requisicao
	and	coalesce(a.dt_baixa::text, '') = ''
	and	coalesce(b.cd_motivo_baixa,0) = 0
	and	a.nr_seq_solic_kit = nr_sequencia_p
	and	b.cd_material = cd_material_w;

	qt_disponivel_w	:= qt_disponivel_w + qt_material_requisicao_w;

	if (qt_disponivel_w < qt_solicitada_w) then
		ds_erro_w := substr(ds_erro_w || cd_material_w || ' - ' || obter_desc_material(cd_material_w) || chr(10),1,1000);
	end if;
	end;
end loop;
close C01;

commit;

if (ds_erro_w IS NOT NULL AND ds_erro_w::text <> '') then
	ds_erro_p := substr(WHEB_MENSAGEM_PCK.get_texto(280396) || chr(10) || ds_erro_w,1,255);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
