SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_material_regulacao ( CD_MATERIAL_P bigint, NR_ATENDIMENTO_P bigint) AS $body$
DECLARE

CD_MATERIAL_W CPOE_MATERIAL.CD_MATERIAL%TYPE;
NR_SEQ_CPOE_ANT_W CPOE_MATERIAL.NR_SEQ_CPOE_ANTERIOR%TYPE;
NR_ATEND_ANTERIOR_W CPOE_MATERIAL.NR_ATENDIMENTO%TYPE;
NR_SEQ_REGULACAO_W  REGULACAO_ATEND.NR_SEQUENCIA%TYPE;

BEGIN
      CD_MATERIAL_W := OBTER_REGRA_SUBST_COM(NULL, CD_MATERIAL_P, NR_ATENDIMENTO_P);
      
      IF (CD_MATERIAL_W IS NOT NULL AND CD_MATERIAL_W::text <> '') THEN 
      
       SELECT MAX(NR_SEQ_CPOE_ANTERIOR)
         INTO STRICT NR_SEQ_CPOE_ANT_W
       FROM CPOE_MATERIAL M 
       WHERE (M.NR_SEQ_CPOE_ANTERIOR IS NOT NULL AND M.NR_SEQ_CPOE_ANTERIOR::text <> '')
         AND M.NR_ATENDIMENTO = NR_ATENDIMENTO_P
         AND M.CD_MATERIAL = CD_MATERIAL_W;

       IF (NR_SEQ_CPOE_ANT_W IS NOT NULL AND NR_SEQ_CPOE_ANT_W::text <> '') THEN
       
         SELECT MAX(NR_ATENDIMENTO) 
           INTO STRICT NR_ATEND_ANTERIOR_W
         FROM CPOE_MATERIAL M
         WHERE M.NR_SEQUENCIA = NR_SEQ_CPOE_ANT_W;

         IF (NR_ATEND_ANTERIOR_W IS NOT NULL AND NR_ATEND_ANTERIOR_W::text <> '') THEN
         
           SELECT A.NR_SEQUENCIA  
             INTO STRICT NR_SEQ_REGULACAO_W
           FROM REGULACAO_ATEND A
           WHERE A.NR_ATENDIMENTO = NR_ATEND_ANTERIOR_W;

         END IF;

         IF (NR_SEQ_REGULACAO_W IS NOT NULL AND NR_SEQ_REGULACAO_W::text <> '') THEN 
           CALL ALTERAR_STATUS_REGULACAO( NR_SEQ_REGULACAO_W, 'FI','');
         END IF;
       END IF;
      END IF;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
