SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_material_substituto (cd_material_p bigint, cd_mat_subst_p text, nr_seq_lote_p bigint) AS $body$
DECLARE


ie_existe_w 			integer;
cd_estabelecimento_w	prescr_medica.cd_estabelecimento%type;
nr_prescricao_w			ap_lote.nr_prescricao%type;

ds_erro_w				varchar(255) := null;


BEGIN
if (coalesce(cd_mat_subst_p,'0') <> '0') then
	select	max(nr_prescricao)
	into STRICT	nr_prescricao_w
	from	ap_lote
	where 	nr_sequencia = nr_seq_lote_p;

	select 	max(cd_estabelecimento)
	into STRICT	cd_estabelecimento_w
	from	prescr_medica
	where 	nr_prescricao = nr_prescricao_w;

	select	count(*)
	into STRICT	ie_existe_w
	from	regra_mat_subst_atend
	where 	cd_material = cd_material_p
	and 	cd_material_subst = cd_mat_subst_p;

	if (ie_existe_w = 0) OR (ds_erro_w IS NOT NULL AND ds_erro_w::text <> '')	then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(140024);
	end if;

end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
