CREATE OR REPLACE PROCEDURE consiste_mat_nao_autorizados ( nr_prescricao_p bigint) AS $body$
DECLARE

ie_possui_mat_nao_consistido_w	varchar(1);

BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	begin
	select	CASE WHEN coalesce(count(*),0)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_possui_mat_nao_consistido_w
	from	prescr_material
	where	nr_prescricao		= nr_prescricao_p
	and	ie_status_cirurgia	= 'CB';
	if (ie_possui_mat_nao_consistido_w = 'S') then
		begin
		CALL excluir_mat_nao_consistidos(nr_prescricao_p);
		end;
	end if;
	end;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
