SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_mat_perfil_exec (cd_perfil_p bigint, cd_material_p bigint, ie_permite_executar_p INOUT text) AS $body$
DECLARE


ie_executar_w			varchar(01);
cd_grupo_material_w		integer;
cd_subgrupo_material_w		integer;
cd_classe_material_w		integer;


c01 CURSOR FOR
	SELECT	ie_permite_executar
	from	perfil_material
	where	cd_perfil						= cd_perfil_p
	and	coalesce(cd_material, cd_material_p)				= cd_material_p
	and	coalesce(cd_grupo_material, cd_grupo_material_w)		= cd_grupo_material_w
	and	coalesce(cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w
	and	coalesce(cd_classe_material, cd_classe_material_w)		= cd_classe_material_w
	order	by  	coalesce(cd_material,0),
			coalesce(cd_classe_material,0),
			coalesce(cd_subgrupo_material,0),
			coalesce(cd_grupo_material,0);



BEGIN

ie_executar_w	:= 'S';

begin
select	cd_grupo_material,
	cd_subgrupo_material,
	cd_classe_material
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w
from	estrutura_material_v
where	cd_material	= cd_material_p;
exception
	when others then
		begin
		cd_grupo_material_w:= '';
		cd_subgrupo_material_w:= '';
		cd_classe_material_w:= '';
		end;
end;

open	c01;
loop
fetch	c01 into ie_executar_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	ie_permite_executar_p	:= ie_executar_w;

	end;
end loop;
close c01;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
