CREATE OR REPLACE PROCEDURE consiste_medico_tipo_atend ( cd_estabelecimento_p bigint, ie_tipo_atendimento_p bigint, cd_medico_resp_p text, nm_usuario_p text, ds_consistencia_p INOUT text) AS $body$
DECLARE

 
QT_REGRA_MEDICO_ATEND_W			bigint;
qt_regra_medico_atend_assist_w		bigint;
qt_regra_medico_Atend_Categ_w	bigint;
qt_regra_medico_Atend_Categ_ww	bigint;
			

BEGIN 
 
select	count(*) 
into STRICT	qt_regra_medico_atend_w 
from	regra_medico_atend 
where	cd_estabelecimento	= cd_estabelecimento_p 
and	ie_tipo_atendimento	= ie_tipo_atendimento_p 
and	ie_corpo_clinico = 'S' 
and 	obter_se_corpo_clinico(cd_medico_resp_p) = 'N';
 
select	count(*) 
into STRICT	qt_regra_medico_atend_assist_w 
from	regra_medico_atend 
where	cd_estabelecimento	= cd_estabelecimento_p 
and	ie_tipo_atendimento	= ie_tipo_atendimento_p 
and	ie_corpo_assistencial = 'S' 
and	obter_se_corpo_assist(cd_medico_resp_p) = 'N';
 
select	count(*) 
into STRICT	qt_regra_medico_Atend_Categ_w 
from	regra_medico_atend a, 
	regra_medico_atend_categ b 
where	a.nr_sequencia	= b.nr_seq_regra 
and	a.cd_estabelecimento	= cd_estabelecimento_p 
and	a.ie_tipo_atendimento	= ie_tipo_atendimento_p 
and	a.ie_corpo_assistencial = 'S' 
and	obter_se_corpo_assist(cd_medico_resp_p) = 'S' 
and	b.nr_seq_categoria	= obter_dados_medico(cd_medico_resp_p,'CAT');
 
 
select	count(*) 
into STRICT	qt_regra_medico_Atend_Categ_ww 
from	regra_medico_atend a, 
	regra_medico_atend_categ b 
where	a.nr_sequencia	= b.nr_seq_regra 
and	a.cd_estabelecimento	= cd_estabelecimento_p 
and	a.ie_tipo_atendimento	= ie_tipo_atendimento_p 
and	a.ie_corpo_clinico = 'S' 
and	obter_se_corpo_clinico(cd_medico_resp_p) = 'S' 
and	b.nr_seq_categoria	= obter_dados_medico(cd_medico_resp_p,'CAT');
 
if	((qt_regra_medico_atend_w > 0) or (qt_regra_medico_atend_assist_w > 0) or (qt_regra_medico_Atend_Categ_ww	> 0) or (qt_regra_medico_Atend_Categ_w	> 0)) then 
	ds_consistencia_p	:= substr(obter_texto_tasy(196875, wheb_usuario_pck.get_nr_seq_idioma),1,255); --'Médico não pode ser responsável pelo atendimento. Verificar regra médico atendimento.'; 
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
