CREATE OR REPLACE PROCEDURE consiste_medico_tipo_servico ( ie_tipo_servico_sus_p bigint, cd_convenio_p bigint, cd_medico_executor_P text, ds_erro_p INOUT text, ie_tipo_servico_p INOUT bigint) AS $body$
DECLARE



ie_conveniado_w	varchar(1) := 'N';



BEGIN

if (ie_tipo_servico_sus_p = 8) and (cd_medico_executor_p IS NOT NULL AND cd_medico_executor_p::text <> '') then
	begin
	ds_erro_p		:= wheb_mensagem_pck.get_texto(279649);
	ie_tipo_servico_p	:= 8;
	end;
end if;

if (cd_medico_executor_p IS NOT NULL AND cd_medico_executor_p::text <> '') and (ie_tipo_servico_sus_p in (7,4)) then
	begin
	select	coalesce(max(ie_conveniado),'N')
	into STRICT	ie_conveniado_w
	from	medico_convenio
	where	cd_convenio	 = cd_convenio_p
	and 	cd_pessoa_fisica = cd_medico_executor_p;

	if	(ie_conveniado_w <> 'S' AND ie_tipo_servico_sus_p = 7) or
		(ie_conveniado_w = 'S' AND ie_tipo_servico_sus_p = 4) then
		ds_erro_p	:= wheb_mensagem_pck.get_texto(279650);
	end if;
	if (ie_conveniado_w <> 'S') then
		ie_tipo_servico_p	:= 4;
	else
		ie_tipo_servico_p	:= 7;
	end if;
	end;
end if;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
