SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_medic_barras_quimio ( ie_lancar_barras_p text, cd_mat_lista_p text, cd_mat_barra_p text, nr_seq_ordem_p bigint, qt_dose_p bigint, qt_dose_real_p bigint, qt_dose_unid_p bigint, qt_dose_medic_ml_p bigint, qt_dose_ml_p bigint, nr_seq_item_prescr_p bigint, cd_unidade_medida_p text, cd_unidade_medida_real_p text, nr_seq_lote_fornec_p bigint, ie_origem_inf_p text, nm_usuario_p text, cd_estabelecimento_p bigint, cd_material_p INOUT bigint, ie_limpar_p INOUT text, ds_erro_p INOUT text) AS $body$
DECLARE

 
ie_consiste_estoque_barras_w	varchar(1);
ie_consiste_pordose_w	varchar(1);
nr_seq_lote_w		bigint;
ds_material_w		varchar(255);
nr_seq_cabine_w		bigint;
qt_estoque_w		double precision;
ds_lixo_w			varchar(255);


BEGIN 
 
ie_limpar_p	:= 'N';
 
SELECT * FROM converte_codigo_barras( 
	cd_mat_barra_p, cd_estabelecimento_p, null, null, cd_material_p, ds_lixo_w, nr_seq_lote_w, ds_lixo_w, ds_lixo_w, ds_lixo_w, ds_lixo_w, ds_lixo_w, ds_lixo_w, ds_lixo_w) INTO STRICT cd_material_p, ds_lixo_w, nr_seq_lote_w, ds_lixo_w, ds_lixo_w, ds_lixo_w, ds_lixo_w, ds_lixo_w, ds_lixo_w, ds_lixo_w;
 
if (coalesce(cd_material_p, 0) = 0) then 
	begin 
	ie_limpar_p	:= 'S';
	ds_erro_p		:= substr(obter_texto_tasy(64331, wheb_usuario_pck.get_nr_seq_idioma),1,255);
	end;
 
elsif (coalesce(nr_seq_lote_w, 0) = 0) then 
	begin 
	ie_limpar_p	:= 'S';
	ds_erro_p		:= substr(obter_texto_tasy(64332, wheb_usuario_pck.get_nr_seq_idioma),1,255);
	end;
 
elsif (position(cd_material_p in cd_mat_lista_p) < 1) then 
	begin 
	 
	CALL grava_log_erro_quimio(cd_material_p, nr_seq_lote_w, nr_seq_ordem_p, nm_usuario_p);
	 
	ie_limpar_p	:= 'S';
	ds_material_w	:= substr(obter_desc_material(cd_material_p), 1,255);
	ds_erro_p		:= substr(obter_texto_dic_objeto(64354, wheb_usuario_pck.get_nr_seq_idioma, 'CD_MATERIAL=' || cd_material_p || ';DS_MATERIAL=' || ds_material_w),1,255);
	end;
 
elsif (ie_lancar_barras_p = 'N') and (qt_dose_unid_p = qt_dose_p) and (qt_dose_medic_ml_p = qt_dose_ml_p) then 
	begin 
	ie_limpar_p	:= 'S';
	ds_erro_p		:= substr(obter_texto_tasy(64451, wheb_usuario_pck.get_nr_seq_idioma),1,255);
	end;
 
else 
	begin 
	select	b.nr_seq_cabine 
	into STRICT	nr_seq_cabine_w 
	from	far_etapa_producao b, 
		can_ordem_prod a 
	where	b.nr_sequencia	= a.nr_seq_etapa_prod 
	and	a.nr_sequencia	= nr_seq_ordem_p;
 
	if (ie_lancar_barras_p = 'S') then 
		begin 
		ds_erro_p := obter_consistencias_quimio( 
			nr_seq_cabine_w, cd_material_p, nr_seq_lote_w, ds_erro_p);
			 
		if (coalesce(ds_erro_p::text, '') = '')	 then 
			CALL inserir_mat_med_ordem( 
				nr_seq_ordem_p, 
				cd_material_p, 
				qt_dose_p, 
				qt_dose_real_p, 
				nr_seq_item_prescr_p, 
				cd_unidade_medida_p, 
				cd_unidade_medida_real_p, 
				nr_seq_lote_fornec_p, 
				ie_origem_inf_p, 
				nm_usuario_p);
		end if;
		 
		end;
	else 
		begin 
 
		select	coalesce(max(qt_estoque),0) 
		into STRICT	qt_estoque_w 
		from	far_estoque_cabine 
		where	cd_estabelecimento	= cd_estabelecimento_p 
		and	nr_seq_cabine	= nr_seq_cabine_w 
		and	cd_material	= cd_material_p 
		and	nr_seq_lote_fornec	= nr_seq_lote_w;
		 
		if	((qt_estoque_w * obter_conversao_unid_med(cd_material_p, cd_unidade_medida_p)) < qt_dose_p) then 
			begin 
			/* Quimioterapia - Parâmetro [187] - Ao bipar medicamentos na manipulação por barras, consistir estoque da cabine */
 
			ie_consiste_estoque_barras_w := obter_param_usuario(3130, 187, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_consiste_estoque_barras_w);
 
			if (ie_consiste_estoque_barras_w = 'S') then 
				begin 
				ie_limpar_p	:= 'S';
				ds_erro_p		:= substr(obter_texto_dic_objeto(64453, wheb_usuario_pck.get_nr_seq_idioma, 'QT_DOSE_UNID=' || qt_dose_unid_p),1,255);
				end;
			end if;
			end;
		end if;
 
		if (coalesce(ds_erro_p::text, '') = '') and (qt_dose_unid_p < qt_dose_p) then 
			begin 
			/* Quimioterapia - Parâmetro [143] - Na opção Medicamento\Diluente Barras, ao bipar consistir dose do medicamento */
 
			ie_consiste_pordose_w := obter_param_usuario(3130, 143, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_consiste_pordose_w);
 
			if (ie_consiste_pordose_w = 'S') then 
				ds_erro_p	:= substr(obter_texto_tasy(64457, wheb_usuario_pck.get_nr_seq_idioma),1,255);
			end if;
			end;
		end if;
		end;
	end if;
	end;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE consiste_medic_barras_quimio ( ie_lancar_barras_p text, cd_mat_lista_p text, cd_mat_barra_p text, nr_seq_ordem_p bigint, qt_dose_p bigint, qt_dose_real_p bigint, qt_dose_unid_p bigint, qt_dose_medic_ml_p bigint, qt_dose_ml_p bigint, nr_seq_item_prescr_p bigint, cd_unidade_medida_p text, cd_unidade_medida_real_p text, nr_seq_lote_fornec_p bigint, ie_origem_inf_p text, nm_usuario_p text, cd_estabelecimento_p bigint, cd_material_p INOUT bigint, ie_limpar_p INOUT text, ds_erro_p INOUT text) FROM PUBLIC;

