SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_minimo_multiplo_req (cd_material_p bigint, qt_requisitada_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE


qt_minimo_multiplo_w		double precision;
qt_fracao_w			double precision;
qt_multiplo_w			double precision;


BEGIN

ds_erro_p		:= '';
select	max(qt_minimo_multiplo_solic)
into STRICT	qt_minimo_multiplo_w
from	material
where	cd_material		= cd_material_p;


qt_multiplo_w	:= trunc(dividir(qt_requisitada_p, qt_minimo_multiplo_w),0);
qt_fracao_w	:= qt_requisitada_p - (qt_multiplo_w * qt_minimo_multiplo_w);

if (qt_fracao_w <> 0) then
	ds_erro_p  := 	WHEB_MENSAGEM_PCK.get_texto(279249) || qt_minimo_multiplo_w ||
			WHEB_MENSAGEM_PCK.get_texto(279250) || qt_fracao_w || ' )';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
