SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_moeda_transf_caixa (nr_seq_caixa_p bigint, ds_lista_movto_p text) AS $body$
DECLARE


cd_moeda_empresa_w	integer;
qt_transacao_w		bigint := 0;


BEGIN

select	obter_moeda_padrao_empresa(cd_estabelecimento,'E')
into STRICT	cd_moeda_empresa_w
from	caixa
where	nr_sequencia = nr_seq_caixa_p;

if (ds_lista_movto_p IS NOT NULL AND ds_lista_movto_p::text <> '') then
	-- Verifica se existem transações em moeda não permitida para o caixa de destino da transferência
	select	count(*)
	into STRICT	qt_transacao_w
	from	movto_trans_financ a
	where	' ' || ds_lista_movto_p || ' ' like '% ' || a.nr_sequencia || ' %'
	and	(ds_lista_movto_p IS NOT NULL AND ds_lista_movto_p::text <> '')
	and	coalesce(a.nr_seq_movto_transf::text, '') = ''
	and	coalesce(a.cd_moeda,cd_moeda_empresa_w) not in (SELECT x.cd_moeda
							from caixa_moedas x
							where x.nr_seq_caixa = nr_seq_caixa_p
							
union

							SELECT cd_moeda_empresa_w
							);
	-- Para o processo se houver transação não aceita
	if (qt_transacao_w > 0) then
		/* Existem transações selecionadas em moeda não aceita pelo caixa de destino. */

		CALL wheb_mensagem_pck.exibir_mensagem_abort(352923);
	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
